import { useState } from "react";
import "./TaskItem.css";

const TaskItem = (props) => {
  const [isDelete, setDelete] = useState(false);
  const onClickHandler = () => {
      setDelete(true);
  };
  const opacityChangeHandler = () => {
    document.getElementById("item").className += 'opacity';
  };

  if(!isDelete){
  return (
    <div className="task-item" id="item"> 
      <div className="task-details">
        <input type="checkbox" id={props.id + ""} onChange={opacityChangeHandler}/>
        <label htmlFor={props.id + ""}>{props.todo}</label>
      </div>
      <div className="delete-task" onClick={onClickHandler}></div>
    </div>
  );
  } else {
    return null; 
  }
};

export default TaskItem;
